mapper.insert:
SELECT nextval('sunportfolio_seq') as test
INSERT INTO sunportfolio ( SEQ, TITLE, COMPANY, GRADE, NAME, TEL, CONTENT, CON_DATE, BOARDCK
,FILENAME
,FILEVOLUME
)VALUES( #{seq}, #{title}, #{company}, #{grade}, #{name}, #{tel}, #{content}, CURRENT_TIMESTAMP, #{boardck}
,#{filename}
,#{fileVolume, jdbcType = BLOB}
)

mapper.select:
select * from (
    select rownum , sun.* from(
        select seq, title, name, content, con_date from sunportfolio order by con_date desc
    ) sun
) where rownum between ${start} and ${end}
